UPDATE tt_content
SET pi_flexform = REPLACE(pi_flexform, '_columns_', '_'),
	pi_flexform = REPLACE(pi_flexform, 'visibility_', 'hide_')
WHERE pi_flexform LIKE '%_columns_%';

SELECT uid, pi_flexform FROM tt_content WHERE pi_flexform LIKE '%segmentation%'
